    private void decActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_decActionPerformed
        String number = mainText.getText() + dec.getText();
        String check = mainText.getText();
        if (check.contains("."))
            number = mainText.getText();
        else
            mainText.setText(number);
        mainText.setText(number);
    }//GEN-LAST:event_decActionPerformed

