    private void opequalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_opequalActionPerformed
        operand2 = Double.parseDouble(mainText.getText());
        if(action.equals("+")){
            total = opearand1 + operand2;
            answer = String.format("%.2f", total);            
        }
        else if (action.equals("-")){
            total = opearand1 - operand2;
            answer = String.format("%.2f", total);
        }
        else if(action.equals("*")){
            total = opearand1 * operand2;
            answer = String.format("%.2f", total); 
        }
        else if(action.equals("/")){
            total = opearand1 / operand2;
            answer = String.format("%.2f", total); 
        }
        else if(action.equals("+")){
            total = opearand1 + operand2;
            answer = String.format("%.2f", total); 
        }
        mainText.setText(answer);
    }//GEN-LAST:event_opequalActionPerformed

